<?php
class Payhow{    private $api_url = "https://api.prod.payhow.com.br/api/v1";    public function getApiUrl()    {        return $this->api_url;    }    public function statusIntegration()    {        $status = false;        $body = [            "merchant_url" => $_SERVER['HTTP_HOST']        ];		$response = wp_remote_post($this->getApiUrl()."/ecommerce/woocommerce/status", array(			'headers'     => array('Content-Type' => 'application/json', 'Accept' => 'application/json'),			'body'        => json_encode($body),			'method'      => 'POST',			'data_format' => 'body',		));        		$return_response = json_decode($response["body"]);                            if ($return_response) {            $status = $return_response->status ? $return_response->status : false;        }		        return $status;    }}?>